    public static getMSSafetyRespAssigned(mSSafetyResponsibilities) {
        if (!isNullOrUndefined(mSSafetyResponsibilities)) {
            mSSafetyResponsibilities.filter(item => {
                return item.IsDeleted == false;
            }).forEach(itm => {
                itm.CombinedName = itm.NameOfResponsible ? itm.NameOfResponsible : (itm.ResponsiblePerson ? itm.ResponsiblePerson.FullName : '');
                let respNames = (itm.Responsibilities.filter(obj => obj.Name.toLowerCase() != 'other').map(p => { if (p.Name.toLowerCase() != 'other') return p.Name }).join(','));
                itm.SubItemText = respNames;
                if (!StringHelper.isNullOrUndefinedOrEmpty(itm.OtherResponsibilityValue)) {
                    if (StringHelper.isNullOrUndefinedOrEmpty(itm.SubItemText)) {
                        itm.SubItemText = itm.OtherResponsibilityValue;
                    } else {
                        itm.SubItemText = itm.SubItemText + ', ' + itm.OtherResponsibilityValue;
                    }
                }
            });
            return mSSafetyResponsibilities.sort((a, b) => a.CombinedName.localeCompare(b.CombinedName));
        }
    }

    public static getMsRiskAssessments(methodStatement:MethodStatement) {
        let msRiskAssessments: RiskAssessment[] = Array.from(methodStatement.MSRiskAssessmentMap ? methodStatement.MSRiskAssessmentMap : []);
        if (!isNullOrUndefined(methodStatement.MSOtherRiskAssessments)) {
            methodStatement.MSOtherRiskAssessments.forEach(x => {
                let newRA: RiskAssessment = new RiskAssessment();
                newRA.Name = x.Name;
                newRA.ReferenceNumber = x.ReferenceNumber;
                newRA.Id = x.Id;
                msRiskAssessments.push(newRA);
            });
            return msRiskAssessments.sort((a, b) => a.ReferenceNumber.localeCompare(b.ReferenceNumber));
        }
    }

